SELECT (SELECT CONCAT(e.FIRST_NAME, ' ', e.LAST_NAME) FROM hr.employees as e WHERE e.EMPLOYEE_ID = jb.EMPLOYEE_ID) AS 'Nome completo', (SELECT j.JOB_TITLE FROM hr.jobs AS j WHERE j.JOB_ID = jb.JOB_ID) AS 'Cargo', jb.START_DATE AS 'Data de in√≠cio do cargo', (SELECT d.DEPARTMENT_NAME FROM hr.departments AS d WHERE d.DEPARTMENT_ID = jb.DEPARTMENT_ID) AS 'Departamento' FROM hr.job_history AS jb WHERE jb.START_DATE <> 0 ORDER BY `Nome Completo` DESC, `Cargo` ASC;